  "thumbnail": "https://api.bookmate.ru/assets/books-covers/16/a4/eKpB1FMX-ipad.jpeg?image_hash=f234ec8f25428376e38b8f8053cb8e08",
  "reader_url": "https://books.yandex.ru/reader/eKpB1FMX?resource=book",
