  "color": 2,
  "created_at": "2024-10-17 20:25:42",
  "book_id": "PIZG6ib7",
  "thumbnail": "https://api.bookmate.ru/assets/books-covers/cd/c9/PIZG6ib7-ipad.png?image_hash=35484f4335e47d3cfefad8b33577915c",
