  "comment": null,
  "color": 2,
  "created_at": "2023-08-09 14:52:49",
  "book_id": "yyCHels9",
  "thumbnail": "https://api.bookmate.ru/assets/books-covers/46/60/yyCHels9-ipad.jpeg?image_hash=1f856da32b26d581418d61dd80b5209a",
